ControlPropertyState.AutoRuleBindingString:
UpdateContext({
    Visible_promotion_detail: true,
    varShowPDF: true
});
Set(
    SelectedPromotion,
    ThisItem
);
Navigate(PromitionDetail2, ScreenTransition.Fade);

"<div style='
  width:98%;
  font-family:Segoe UI, Arial;
  font-size:15px;
  color:#1a1a1a;
  border:1.5px solid #cce4ff;
  border-radius:10px;
  padding:8px 10px;
  background-color:#f9fcff;
  box-shadow:0px 2px 4px rgba(0,0,0,0.04);
  line-height:1.3;
  box-sizing:border-box;
  margin:auto;
  overflow:hidden;
'>

  <!-- Promotion Title -->
  <div style='
    font-weight:700;
    color:#04a1b3;
    font-size:15px;
    margin-bottom:4px;
    word-wrap:break-word;
    display:flex;
    align-items:center;
    gap:8px;
    flex-wrap:wrap;
  '>
    " & ThisItem.Promotion &

    // 🔴 Ưu tiên hiển thị “SẮP HẾT HẠN” nếu còn <=72 giờ (>=0)
    If(
      !IsBlank(ThisItem.'End date') &&
      DateDiff(
        DateAdd(Now(), 7, TimeUnit.Hours),
        DateAdd(ThisItem.'End date', 7, TimeUnit.Hours),
        TimeUnit.Hours
      ) >= 0 &&
      DateDiff(
        DateAdd(Now(), 7, TimeUnit.Hours),
        DateAdd(ThisItem.'End date', 7, TimeUnit.Hours),
        TimeUnit.Hours
      ) <= 72,
      "<span style='
        background:linear-gradient(90deg,#ff3b3b,#ff8a8a);
        color:#ffffff;
        font-size:11px;
        font-weight:700;
        padding:3px 9px;
        border-radius:12px;
        margin-left:4px;
        letter-spacing:0.5px;
        text-transform:uppercase;
        box-shadow:0px 2px 4px rgba(255,59,59,0.4);
      '>SẮP HẾT HẠN</span>",

      // 🟠 Nếu không, kiểm tra NEW (Start date trong 7 ngày)
      If(
        !IsBlank(ThisItem.'Start date') &&
        DateDiff(ThisItem.'Start date', Today(), TimeUnit.Days) <= 7 &&
        DateDiff(ThisItem.'Start date', Today(), TimeUnit.Days) >= 0,
        "<span style='
          background:linear-gradient(90deg,#ff6a00,#ffb347);
          color:#ffffff;
          font-size:11px;
          font-weight:700;
          padding:3px 9px;
          border-radius:12px;
          margin-left:4px;
          letter-spacing:0.5px;
          text-transform:uppercase;
          box-shadow:0px 2px 4px rgba(255,106,0,0.4);
        '>NEW</span>",
        ""
      )
    ) &
  "</div>

  <!-- Start - End Date -->
  " & If(
      !IsBlank(ThisItem.'Start date') || !IsBlank(ThisItem.'End date'),
      "<div style='margin-bottom:4px; display:flex; align-items:center; flex-wrap:wrap;'>
        <span style='font-size:13px; margin-right:4px;'>🕒</span>
        <span><b>Start - End:</b> " &
          If(!IsBlank(ThisItem.'Start date'),
             Text(ThisItem.'Start date', "dd/mm/yyyy"),
             ""
          ) &
          If(!IsBlank(ThisItem.'End date'),
             " - " & Text(ThisItem.'End date', "dd/mm/yyyy"),
             ""
          ) &
        "</span>
      </div>",
      ""
    ) & "
</div>"
